library(shiny)

# ===== Function to select random name =====
SelectName <- function(data, data_column, data_value) {
  filtered_data <- subset(data, data[[data_column]] == data_value)
  if (nrow(filtered_data) == 0) {
    return("No name found for the selected period.")
  }
  name_list <- filtered_data["Name"]
  random_name <- name_list[sample(1:nrow(name_list), 1), ]
  return(random_name)
}

# ===== UI =====
ui <- fluidPage(
  tags$head(
    tags$style(HTML("
      #selected_name {
        font-size: 60px;
        font-weight: bold;
        color: #2c3e50;
      }
      #timeleft {
        font-size: 60px;
        font-weight: bold;
        color: #2c3e50;
      }
      .sidebar-section {
        padding: 20px;
        margin-bottom: 40px;
        border: 1px solid #ddd;
        background-color: #f7f7f7;
        border-radius: 20px;
      }
      .sidebar-section h4 {
        margin-top: 0;
      }
      .main-panel-spacing {
        margin-top: 50px;
      }
    "))
  ),
  
  titlePanel("Random Name & Timer"),
  
  sidebarLayout(
    sidebarPanel(
      wellPanel(
        class = "sidebar-section",
        h4("Upload CSV Data"),
        fileInput("file", "Choose CSV File",
                  accept = c(".csv")),
        helpText("CSV must have columns: Name, Period")
      ),
      
      wellPanel(
        class = "sidebar-section",
        h4("Random Name Generator"),
        uiOutput("period_ui"),
        actionButton("generate", "Generate Random Name")
      ),
      
      wellPanel(
        class = "sidebar-section",
        h4("Timer"),
        numericInput("minutes", "Minutes:", value = 0, min = 0),
        numericInput("seconds", "Seconds:", value = 10, min = 0, max = 59),
        actionButton("start", "Start Timer"),
        actionButton("stop", "Stop Timer"),
        actionButton("reset", "Reset Timer")
      )
    ),
    
    mainPanel(
      h3("Selected Name:"),
      textOutput("selected_name"),
      tags$div(style = "margin-top: 50px;"),
      h3("Timer Countdown:"),
      textOutput("timeleft")
    )
  )
)

# ===== Server =====
server <- function(input, output, session) {
  
  # Reactive value for the uploaded data
  data <- reactive({
    req(input$file)
    read.csv(input$file$datapath, stringsAsFactors = FALSE)
  })
  
  # Reactive for selected name
  selected_name <- reactiveVal("Click to Start")
  
  # Timer reactive values
  timer_value <- reactiveVal(0)
  active <- reactiveVal(FALSE)
  
  format_time <- function(seconds) {
    mins <- floor(seconds / 60)
    secs <- seconds %% 60
    sprintf("%02d:%02d", mins, secs)
  }
  
  # Timer countdown
  observe({
    invalidateLater(1000, session)
    isolate({
      if (active()) {
        new_val <- timer_value() - 1
        timer_value(new_val)
        if (new_val <= 0) {
          active(FALSE)
          timer_value(0)
          showModal(modalDialog(
            title = "Countdown Complete",
            "Time's up!"
          ))
        }
      }
    })
  })
  
  # Start Timer
  observeEvent(input$start, {
    total_seconds <- input$minutes * 60 + input$seconds
    timer_value(total_seconds)
    active(TRUE)
  })
  
  # Stop Timer
  observeEvent(input$stop, {
    active(FALSE)
  })
  
  # Reset Timer
  observeEvent(input$reset, {
    total_seconds <- input$minutes * 60 + input$seconds
    timer_value(total_seconds)
    active(FALSE)
  })
  
  # Display timer
  output$timeleft <- renderText({
    format_time(timer_value())
  })
  
  # Dynamic UI for period selection
  output$period_ui <- renderUI({
    req(data())
    selectInput("period", "Select period:",
                choices = unique(data()$Period))
  })
  
  # Generate random name
  observeEvent(input$generate, {
    req(data())
    name <- SelectName(data(), "Period", input$period)
    selected_name(name)
  })
  
  # Display name
  output$selected_name <- renderText({
    selected_name()
  })
}

# Run ShinyLive-compatible app
shinyApp(ui, server)
